import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
// import { Link } from "react-scroll";
import '../components/styles/style.css';
import config from '../config';
import Profile_icons from "../assets/User_icons.png";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
// import Modal from "react-bootstrap/Modal";
// import { Button } from "react-bootstrap";
import { makeStyles,Button } from '@material-ui/core';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
// import ActivityNotifications from './ActivityNotifications'
import NotificationsNoneIcon from '@material-ui/icons/NotificationsNone';

import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import { store } from 'react-notifications-component';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

const FilterNav = ({ Logo }) => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const closeModal = () => {
    setOpen(false);
    setAnchorEl(null);
  };
  // console.log(navdata)
  const [isOpen, setIsOpen] = useState(false);
  const [nav, setNav] = useState(false);
  const handleScroll = useCallback(() => {
    if (window.pageYOffset > 140) {
      if (!nav) {
        setNav(true);
      }
    } else {
      if (nav) {
        setNav(false);
      }
    }
  }, [nav]);



  const fetchURL = config.drupal_url+'/FilterPageHeadFooter';

  const [HomeNav, setItems] = useState();

  // const [show, setShow] = useState(false);
  // const handleShow = () => setShow(true);
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
    // setShow(false);
    console.log("good");
  };

  const handleLogOut = () => {
    window.sessionStorage.setItem("login", "false");
    console.log("good day");
    setAnchorEl(null);
    // setShow(false);
  };

  const Notifications = () => {
    store.addNotification({
      title: "Wonderful!",
      message: "teodosii@react-notifications-component",
      type: "success",
      insert: "top",
      container: "top-right",
      animationIn: ["animate__animated", "animate__fadeIn"],
      animationOut: ["animate__animated", "animate__fadeOut"],
      dismiss: {
        duration: 5000,
        onScreen: true
      }
    });

    if(window.sessionStorage.getItem("Notifications") === "true"){
      window.sessionStorage.setItem("Notifications", "false");
    }else{
      window.sessionStorage.setItem("Notifications", "true");
    }
    console.log(window.sessionStorage.getItem("Notifications"));
  }


  useEffect(() => {
    // window.addEventListener("scroll", handleScroll);
     const getItems = () => fetch(fetchURL).then(res => res.json());
    getItems().then(data => setItems(data));
 }, [fetchURL,setItems]);

  let params = new URLSearchParams((window.location).search);
  if (params.get("login") === "true") {
    window.sessionStorage.setItem("login", "true");
  }
  return (
    <Nav onScroll={handleScroll}>
      <Container>
      <img
                    src={`${config.drupal_url}/${Logo.website_logo}`}
                    alt="Skill Portal"
                    style={{height:'60px'}}
                ></img>

        {/* <LogoIcon></LogoIcon> */}
        {console.log("login:" + window.sessionStorage.getItem("login"))}
        <Humburger onClick={() => setIsOpen(!isOpen)}>
          <span></span>
          <span></span>
          <span></span>
        </Humburger>
        <Menu1 isOpen={isOpen}>

          <LinkWrapper >
          {/* <ReactNotification></ReactNotification> */}
            {HomeNav && HomeNav.map((data, index) => (
                //  <Link  id="menu" key={index} to={data.field_navigation_link}> <MenuLink>{data.title} </MenuLink></Link> 
                 <a href={data.field_navigation_link} activeClass="active"><MenuLink>{data.title} </MenuLink></a>
            //   <Link id="menu" key={index} activeClass="active" to={data.field_navigation_link}
            //     spy={true}
            //     smooth={true}
            //     offset={-70}
            //     duration={800}>
                // <MenuLink  >
                //   {data.title}
                // </MenuLink>
            //   </Link>
            ))}
             <a  activeClass="active" href={config.drupal_url+"/admin/content"}> <MenuLink  >
                 Dashboard
                </MenuLink></a>
                <div onClick={Notifications}><NotificationsNoneIcon></NotificationsNoneIcon></div>   
            {window.sessionStorage.getItem("login") === "true" ?
              <>
                <img
                  className="Profile_icons"
                  src={Profile_icons}
                  alt="Profile"
                  onClick={handleClick}
                ></img>
                <Menu
                  id="simple-menu"
                  anchorEl={anchorEl}
                  keepMounted
                  open={Boolean(anchorEl)}
                  onClose={handleClose}
                  style={{ top: "40px", right: "5%" }}
                >
                  <MenuItem onClick={handleClose}><a href={`${config.drupal_url}/node/add/employee`} className="Profile-Nav" style={{ textDecoration: "none" }}>Profile</a></MenuItem>
                  {/* <MenuItem onClick={handleClose}>My account</MenuItem> */}
                  <MenuItem onClick={handleOpen}>Logout</MenuItem>
                </Menu>

                <Modal
                  aria-labelledby="transition-modal-title"
                  aria-describedby="transition-modal-description"
                  className={classes.modal}
                  open={open}
                  onClose={closeModal}
                  closeAfterTransition
                  BackdropComponent={Backdrop}
                  BackdropProps={{
                    timeout: 500,
                  }}
                >
                  <Fade in={open}>
                    <div className={classes.paper}>
                    Are you sure want to Logout the account!<br></br>
                    <Button style={{ margin: '10px' }} variant="secondary" onClick={closeModal} onChange={handleLogOut}>
                      No,Wait
                    </Button>
                    <Button style={{ margin: '10px', backgroundColor: '#ef6e25', border: "#ef6e25", borderRadius: "10%" }} variant="primary" onClick={handleLogOut}>
                      Yes,Logout
                    </Button>
                    </div>
                  </Fade>
                </Modal>

              </>
              : <Button1><a href={`${config.drupal_url}/user/login`} style={{ color: 'white' }}>LOGIN</a></Button1>}
          </LinkWrapper>
        </Menu1>
      </Container>
    </Nav>
  );
};

export default FilterNav;
const Container = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  align-items: center;
  flex-wrap: wrap;
  max-width: 100%;
  margin: auto;
  padding: 0;
  img{
    cursor: Pointer;
  }
  a {
    text-decoration: none;
    color: #000000;
    font-size: 18px;
    font-family: Montserrat;
    padding: 0.7rem 1.5rem;
    transition: all 0.2s ease-in;
    font-wegiht: 500;
    &:hover {
      color: #ef6e25;
      background: #e7e9fc;
    }

  }
`;


const Nav = styled.div`
  height: 5%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
  z-index: 3;
  background-color: ${({ nav }) => (nav ? "black" : "white")};
`;

const Menu1 = styled.div`
  diplay: flex;
  justy-content: space-between;
  align-item: center;
  position: relative;
  @media (max-width: 768px) {
    background-color: rgba(255, 255, 255, 0.9);
    @supports (-webkit-backdrop-filter: none) or (backdrop-filter: none) {
      -webkit-backdrop-filter: blur(35px);
      backdrop-filter: blur(15px);
      background-color: rgba(255, 255, 255, 0.4);
    }
    border-radius: 1rem;
    margin-top: 1rem;
    box-shadow: -4px 8px 15px 1px rgba(0, 0, 0, 0.07);
    overflow: hidden;
    flex-direction: column;
    max-height: ${({ isOpen }) => (isOpen ? "300px" : "0")};
    transition: max-height 0.3s ease-in;
    width: 100%;
  }
`;
const LinkWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
