import React from 'react'
import styled from 'styled-components';
import './styles/bubbles.css'
const HomeText = ({welcome}) => {
    console.log(welcome);
    return (
        <Container> 
            {welcome.map(x =>(<h1>{x.banner_content.split(/<\/br\>/g)}</h1>))}
        </Container>
    )
}

export default HomeText

const Container=styled.div`
width:100%;
padding-right:5%;
h1 {
    color:#939191;
    width:auto;
    font-family: Montserrat;
    margin:0px;
    padding-bottom:1%;
    font-size:2.1rem;
    &:nth-of-type(1){
        font-weight:700;
        @media (max-width:400px){
            font-weight:700;
            font-size:1.8rem;
        }
    }
    &:nth-of-type(2){
        font-size:1.5rem;
        @media (max-width:400px){
            font-size:1rem;
        }
    }
    &:nth-of-type(3) {
        font-size:1.5rem;
        margin:0px auto;
        @media (max-width:400px){
            font-size:1rem;
        }
      }
    }
    @media (max-width:400px){
        height:100%;
    }

`;
