import React from 'react'
import classes from './styles/Projects.module.css'
const Projects=[{
    name:'XXX',
},
{
    name:'YYY',
},
{
    name:'ZZZ',
},
];

class Cards extends React.Component {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    componentDidMount() {
        this.props.amLoaded();
    }

    get_coords = (theta, radius) => {
        return {
            x: Math.cos(theta) * radius,
            y: Math.sin(theta) * radius
        }
    }

    render() {
        let coord = this.get_coords(this.props.theta, this.props.radius);

        return (
            <div className={classes.cards} style={{
                ...styles.cards,
                left: `${this.props.center.x + coord.x}px`,
                top: `${this.props.center.y - coord.y}px`
            }}>
                <h1 style={{marginTop:"75%",marginLeft:"1px",fontSize:"1rem"}}>xxx</h1>
            </div>
        )
    }
}
const styles = {
    card: {
        margin: '0px',
        padding: '0',
        width: '100px',
        height: '100px',
        borderRadius: '50%',
        position: 'absolute',
        transform: 'translate(-50%, -50%)',
        backgroundColor: 'black',
        color: 'black',
        textAlign: 'center', 
        fontSize: '0.7rem', 
    },
    image: {
        width: '100%',
        height: '100%',
        borderRadius: '50%'
    }
}

export default React.memo(Cards);
