import React, { Component } from 'react';
import '../components/styles/FilterPage.css';
import config from '../config';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';


export default class EmployeeView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Employees: [],
      skillsApi: [],
      skills: [],
      name: '',
      Experience: null,
      PrimarySkill: '',
      ExperienceManuallyMax: null,
      ExperienceManuallyMin: null,
      checkedItemsSkills: new Map(),
      checkedItemsManager: new Map(),
      checkedItemsPod: new Map(),
      ExperienceMinMax: [0, 47],
    }

    this.SkillhandleChange = this.SkillhandleChange.bind(this);  
    this.ManagerHandleChange = this.ManagerHandleChange.bind(this);  
    // this.handleSubmit = this.handleSubmit.bind(this);  
  }

  componentDidMount() {
    Promise.all([
      fetch(config.drupal_url+'/lister/Employees').then(res => res.json()),
      fetch(config.drupal_url+'/lister/Taxonomy/skills').then(res => res.json())
    ]).then(([urlOneData, urlTwoData]) => {
      this.setState({
        Employees: urlOneData,
        skillsApi: urlTwoData
      });
    })
  }

    valueText = (value) => {
        return `${value}`;
    };
    handleChange = (e, newValue) => {
        console.log(newValue);
        this.setState({ ExperienceMinMax: newValue });
    };

  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  }


  SkillhandleChange(event) {   //skills checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsSkills: prevState.checkedItemsSkills.set(item, isChecked) }));
  }

  ManagerHandleChange(event) {   //skills checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsManager: prevState.checkedItemsManager.set(item, isChecked) }));
  }

  // handleSubmit(event) {
  //   console.log(this.state);
  //   event.preventDefault();
  // }



  render() {
    // this.state.skillsApi.map(x => this.state.skills.push(x.name))
    let Employees = this.state.Employees;
    let experienceMin, experienceMax;
    let expMinReq = this.state.ExperienceMinMax[0];
    let expMaxReq = this.state.ExperienceMinMax[1];
    let nameReq = this.state.name;
    let PrimarySkillReq = this.state.PrimarySkill;
    // let ExperienceManuallyMaxReq = this.state.ExperienceManuallyMax;
    // let ExperienceManuallyMinReq = this.state.ExperienceManuallyMin;
    let primaryskillsChecked = this.state.checkedItemsSkills;
    let ManagersChecked = this.state.checkedItemsManager;
    // this.state.skills.filter(onlyUnique);removeusingSet(array)
    console.log((this.state.checkedItemsSkills));



    // if (ExperienceManuallyMaxReq || ExperienceManuallyMinReq) {  //checking whether customer entered the custom experience or not
    //   experienceMin = ExperienceManuallyMinReq;
    //   experienceMax = ExperienceManuallyMaxReq;
    //   if (!ExperienceManuallyMaxReq) {
    //     experienceMax = 100;
    //   } else if (!ExperienceManuallyMinReq) {
    //     experienceMin = 0
    //   }
    // } else {
    //   if (!expReq) {
    //     experienceMin = 0;
    //     experienceMax = 100;
    //     console.log(expReq);
    //   } else {
    //     var exp = expReq.split("-");
    //     experienceMin = exp[0];
    //     experienceMax = exp[1];
    //   }
    // }

    // console.log(experienceMax);
    // console.log(experienceMin);
    return (
      <div className="filter-container">
        <div className="reqForm">
          <form >
            <p className="filterName">Enter name:</p>
            <input
              type='text'
              name='name'
              onChange={this.myChangeHandler}
              className="InputtextField" 
            />
            <div className="Experience-overview">
            <p className="filterName">Experience: </p>
            <p> {this.state.ExperienceMinMax[0]} </p>
              <p>  {this.state.ExperienceMinMax[1]} </p>
            </div>
            <div>
                <Typography id="range-slider" gutterBottom>
                {/* Experience */}
                {/* <p>{this.state.ExperienceMinMax[0]} 
                {this.state.ExperienceMinMax[1]}</p> */}
                </Typography>
                <Slider
                defaultValue={[0, 47]}
                value={this.ExperienceMinMax}
                onChange={this.handleChange}
                valueLabelDisplay="auto"
                aria-labelledby="range-slider"
                getAriaValueText={this.valueText}
                step={1}
                min={0}
                max={40}
                />
            </div>

            {/* Priamry skills */}
            <p className="filterName">Skills :</p>


            <form >
            <table className="PrimarySkillsTableCB">
              {

                this.state.skillsApi.map(item => (
                  <tr>
                    <td>
                  {/* <li>? */}
                    <label>
                      <input
                        type="checkbox"
                        value={item.name.toLowerCase()}
                        onChange={this.SkillhandleChange}
                      /> {item.name}
                    </label>
                   {/* </li> */}
                   </td>
                   </tr>
                ))
              }
            </table>
            </form>
            {/* <input
              type='text'
              name='PrimarySkill'
              onChange={this.myChangeHandler}
              className="InputtextField" 
            /> */}


          </form>
        </div>

        <div className="Employees">
          {Employees.map(x => (
            <>
              {(
                (() => {
                  var experienceMatch = false;
                  var primarySkillsMatch = false;
                  var secondarySkillsMatch = false;
                  var nameMatch = false;
                  var idMatch = false;
                  var managerMatch = false;
                  //experience checking: whether the emp has meet the req experience of customer
                  if ((((parseInt(x.Experience)) >= expMinReq) && (parseInt(x.Experience) <= expMaxReq))) {
                    experienceMatch = true;
                  }

                  //name checking: whether the emp has matched the name
                  if (nameReq) {
                    if (((x.name.toLowerCase()).includes(nameReq.toLowerCase()))) {
                      nameMatch = true;
                    }
                  } else {
                    nameMatch = true;     //if customer not typed anything
                  }

                  //Primary skills checking: whether the emp has req primary skills
                  if (PrimarySkillReq) {
                    if ((x.primary_skills.toLowerCase()).includes(PrimarySkillReq.toLowerCase())) {
                      primarySkillsMatch = true;
                    }
                  } else {
                    primarySkillsMatch = true;
                  }

                  // skills checking: whether the emp has req  skills

                  if (primaryskillsChecked.size >0) {
                    const iterator1 = primaryskillsChecked.keys();
                    const iterator2 = primaryskillsChecked.values();

                    // console.log(iterator1.next().value);
                    // console.log(iterator2.next().value);
                    for(let i=0; i<primaryskillsChecked.size;i++){

                        if(iterator2.next().value){
                          // console.log(iterator1.next().value);
                          let ps = iterator1.next().value;
                          console.log(ps);
                          console.log(x.primary_skills.toLowerCase());

                          if ( ((x.primary_skills.toLowerCase()).includes(ps)) || ((x.secndary_skills.toLowerCase()).includes(ps))) {

                            nameMatch = true;
                            console.log("true");
                          }else{
                            console.log("false");
                            return (<></>);
                          }

                        }else{
                          console.log(iterator1.next().value);  //don't uncomment this line if u want remove console.log
                        }
                    }
                  } else {
                    nameMatch = true;
                  }


                  if (experienceMatch && nameMatch && primarySkillsMatch) {

                    return (
                      (<div className="Employee-details">
                        {/* {nameReq}
                        { console.log(x.Experience )} 
                        { console.log(experienceMin )} 
                        { console.log(experienceMax )}  */}

                        <img src={`http://localhost/${x.Profile_photo}`}
                          height='200' alt="profilephoto"
                          className="profilephoto" />
                        {/* {x.name} */}
                        <div className="Employee-description">
                          <p className="Name">{x.name}</p>
                          {/* <p>{ x.Emp_id }</p> */}
                          <p>{x.role}</p>
                          <p>Manager: {x.Manager}</p>

                          <p>Experience: {x.Experience} years</p>

                        </div>
                        <button className="Details-Btn"><a href={'/EmployeeDetails/' + x.Emp_id}>Show Details</a></button>
                        {/* <Link to={{ pathname: "/EmployeeView", state: '{ x.Emp_id }' }}>{ x.name }</Link> */}

                      </div>)
                    )
                  }
                })()
                // (( ((x.Experience) >= experienceMin) && ((x.Experience) <= experienceMax)) && ((nameReq === '') ? true : ((x.name.toLowerCase()).includes(nameReq)))
                //   && ((PrimarySkillReq === '') ? true : ((x.primary_skills.toLowerCase()).includes(PrimarySkillReq)))) ?

                // : (<></>)
              )}


            </>
          ))
          }
        </div>
      </div>
    );
  }
}
