import React from "react";
import Cards from "./Cards";

class Wheels extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            radius: 150,
            cards: [],
            theta: 0.0,
            snap_point: { x: null, y: null },
            snap_in_progress: false,
            children_loaded: 0,
            loaded: false,
        }

        this.temp_theta = 0.0;
        this.anim_id = null;
    }

    componentDidMount() {
        let center_of_wheel = {
            x: parseFloat(this.wheel.style.width) / 2,
            y: parseFloat(this.wheel.style.height) / 2
        }
        let temp_cards = [];

        for (let i = 0; i < 8; i++) {
            temp_cards.push(
                // <Card radius={this.state.radius} theta={(Math.PI / 4) * i} center={center_of_wheel} key={`card_${i}`} pic={`./${i}-200x200.jpg`} amLoaded={this.children_loaded} />
                <Cards radius={this.state.radius} theta={(Math.PI / 4) * i} center={center_of_wheel} key={`card_${i}`} pic={`https://picsum.photos/500/500`} amLoaded={this.children_loaded} />
            );
        }

        this.setState({ cards: temp_cards });
    }
}

export default Wheels;
