import React, { Fragment , useState} from "react";
import randomcolor from "randomcolor";
import {
    useParams,
  } from "react-router-dom";
import "../components/styles/orgChartStyles.css"

const Chart = () => {
    const levelColor = randomcolor();
    let params = useParams();
    let OrgChartArray = [];
    let head =[];
    if(params.Emp === "default"){
         head = [
            {
            "name": "Srinivasan Gopal",
            "emp_id": "0001",
            "role": "Chief Executive Officer",
            "DP": "/sites/default/files/default_images/DefaultDp_0.png",
        }];

        const Presidents = [
            {
                "name": "Vasan Sampath",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0011",
                "DP": "/sites/default/files/2021-07/chris-gayle_0.png",
                "role": "President"
                },
                {
                "name": "Mukund Devarajan",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0012",
                "DP": "/sites/default/files/2021-07/rishabh-rajendra-pant_0.png",
                "role": "Senior Vice President"
                },
                {
                "name": "Vijay Ramanan",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0013",
                "DP": "/sites/default/files/2021-07/rohit-sharma.png",
                "role": "Senior Vice President"
                }
        ]


        // OrgChartArray.push(head);
        // OrgChartArray.push(Presidents);
        window.sessionStorage.setItem("Reporting", JSON.stringify(Presidents));
        // window.sessionStorage
    }
    console.log( params.Emp);
    // params.Emp
    return (
      <div className="org-tree">

      <Card data={head} />
   </div>

    )
}

export default Chart;

const Card = (props) => {
  const levelColor = randomcolor();
  console.log(props);

  const newChart  = (Emp) => {
    let EmpArr = [];
    EmpArr.push(JSON.parse(Emp));
    window.sessionStorage.setItem("emp", JSON.stringify(EmpArr))
    console.log(JSON.parse(window.sessionStorage.getItem("emp")));
    window.location.href = "/OrgChart/" + JSON.parse(window.sessionStorage.getItem("emp"));
    // console.log(JSON.parse(EmpArr));
    // setnewRender(true)
  //  return  <Chart data={JSON.parse(Emp)} ></Chart>
  }

  return (
    <>
    <ul>
      {props.data.map((item) => (
        <Fragment key={item.name}>
          <li>
            <div className="OrgCard"  onClick={() => newChart(JSON.stringify(item))}>
              <div className="image">
                {console.log(item)}
                {console.log(props.data.length)}
                <img src={`https://dev-employeeportal.pantheonsite.io/${item.DP}`}   alt="Profile"
                  style={{ borderColor: levelColor }}></img>
              </div>
              <div className="OrgCard-body">
                <h4 className="OrgCard-body-h4">{item.name}</h4>
                <p className="OrgCard-body-p">{item.role}</p>
              </div>
              <div></div>
            </div>
            {props.data.length === 1 ? <Card data={JSON.parse(window.sessionStorage.getItem("Reporting"))} /> : <></>}
          </li>
        </Fragment>
      ))}
    </ul>
    </>
  );
};
