import React, { useState  , useEffect} from "react";
import classes from "./styles/Projects.module.css";
import './styles/cards.css';
import Target_icon from '../assets/target_icon.png';

const fetchURL = "http://localhost/lister/Taxonomy/Skills";
const Skills = () => {
  const[state , setState] = useState(false);
  const [skillData, setskillData] = useState([]);
  console.log(state)
  const dummy = skillData;

    useEffect(()=>{
      setInterval((()=>{
        const temp = dummy[0];
        dummy.shift();
        dummy.push(temp);
        setState((prevstate) => !prevstate)
      }),2000);

      const fetchFuntion = async () => {
        const data = await fetch(fetchURL);

        if (data.ok) {
          const res = await data.json();
          setskillData(res);
        }
      };
      fetchFuntion();

    },[dummy])
    console.log(dummy[0])
  return (
    <>
    <div className={classes.wheels} id="skills"></div>
    <a href="/" className={classes.btn_s}>Skills</a>
    { dummy[0] &&<> <div className={classes.cards} id="skill1">
        {dummy[0].name &&<p id="c_title1">{dummy[0].name}</p>}
      </div>
      <div className={classes.cards} id="skill2" >
      <img src={Target_icon} alt="active card" id="target_icon"></img>
        {dummy[1].name&&<p id="c_title2">{dummy[1].name}</p>}
      </div>
      <div className={classes.cards} id="skill3">

        {dummy[2].name&&<p id="c_title3">{dummy[2].name}</p>}
      </div>
      <div className={classes.cards} id="skill4">
        {/* {dummy[3].name&&<p id="c_title4">{dummy[3].name}</p>} */}
      </div></>}


    </>
  );
};

export default Skills;
