import React from 'react'
import classes from './styles/Projects.module.css'
import Cards from './Cards'
class Wheels extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            radius: 150,
            cards: [],
            theta: 0.0,
            snap_point: { x: null, y: null },
            snap_in_progress: false,
            children_loaded: 0,
            loaded: false,
        }

        this.temp_theta = 0.0;
        this.anim_id = null;
    }

    componentDidMount() {
        let center_of_wheel = {
            x: parseFloat(this.wheels.style.width) / 2,
            y: parseFloat(this.wheels.style.height) / 2
        }
        let temp_cards = [];

        for (let i = 0; i < 8; i++) {
            temp_cards.push(
                // <Card radius={this.state.radius} theta={(Math.PI / 4) * i} center={center_of_wheel} key={`card_${i}`} pic={`./${i}-200x200.jpg`} amLoaded={this.children_loaded} />
                <Cards radius={this.state.radius} theta={(Math.PI / 4) * i} center={center_of_wheel} key={`card_${i}`} />
            );
        }

        this.setState({ cards: temp_cards });
    }
    render() {
        return (
            <div style={styles.wheel}>
                {this.state.cards}
            </div>
        )

    }

}

const styles = {
    wheels: {
        margin:'70px',
        height: '100px',
        width: '100px',
        borderRadius: '50%',
        position: 'absolute',
        transform: 'translate(-50%, -50%)',
        backgroundColor: '#EF6E25',
        animation: 'spin 5s',
        animationDuration: '5s',
        animationTimingFunction: 'ease',
    animationIterationCount: 'infinite'
    }
}

export default Wheels
