import React,{useEffect,useState, useCallback}  from "react";
import config from './config';



export default function utility() {
    return (
        <div>
            utility
        </div>
    )
}

export const EmployeeApi = () =>{
    const fetchURL = config.drupal_url+'/Employees';
    const [items, setItems] = useState();

    useEffect(() => {
      const getItems = () => fetch(fetchURL).then(res => res.json());
       getItems().then(data => setItems(data));
    }, [fetchURL,setItems]);

    return items;
}

export const UserApi = () =>{
    const fetchURL = config.drupal_url+'/users';
    const [items, setItems] = useState(null);

    useEffect(() => {
      const getItems = () => fetch(fetchURL).then(res => res.json());
       getItems().then(data => setItems(data));
    }, [fetchURL]);

    console.log(items);
    if(items){
     window.sessionStorage.setItem("user", JSON.stringify(items[0]));
     window.sessionStorage.setItem("userName", items[0].name);
     window.sessionStorage.setItem("userLoginId", items[0].uid);
    }

    return items;
}

export const useFetch = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchDetails = useCallback(async (url, resultData) => {
    setLoading(true);
    try {
    const result = await fetch(url, {
    method: "GET",
    });
    if (!result.ok) {
    throw new Error("Request failed!");
    }

    let data = await result.json();
    if (data != null) {
    setLoading(false);
    setError(null);
    } else if (data.length === 0) {
    throw new Error("Centers Not Available!");
    }
    resultData(data);
    } catch (error) {
    setError(error.message + "");
    setLoading(false);
    }
    }, []);
    return [loading, fetchDetails, error];
   };
