import Cards from './Cards'
const Projects=[{
    name:'XXX',
},
{
    name:'YYY',
},
{
    name:'ZZZ',
},
];

class Wheels extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            radius: 150,
            cards: [],
            theta: 0.0,
            snap_point: { x: null, y: null },
            snap_in_progress: false,
            children_loaded: 0,
            loaded: false
        }

        this.temp_theta = 0.0;
        this.anim_id = null;
    }

    componentDidMount() {
        let center_of_wheel = {
            x: parseFloat(this.wheel.style.width) / 2,
            y: parseFloat(this.wheel.style.height) / 2
        }
        let temp_cards = [];

