import React from "react";
import perfect from "../assets/perfect.png";
import search from "../assets/search.png";
import share from "../assets/share.png";
import styled from "styled-components";

const Conatiner = styled.div`
    display:flex;
    flex-direction:column;
    font-family: Montserrat;
    width:100%
    height: 250vh;
    background-color: #F5F5F5;
  `;

const Left = styled.div`
  width: 50%;
  margin: 30px;
  padding: 35px;
  img {
    @media (max-width:400px){
      height:50%;
      width:50%;
    }
  }
  h1 {
    color:#939191;
    font-size: 2rem;
    font-weight: 600;
  }
  p {
    font-size: 1.5rem;
    margin-left: 15px;
  }
  @media (max-width:400px){
    width:100%;
    margin:0;
    padding:0;
    font-size:1rem;
}


`;

const Right = styled.div`
  width: 50%;
  margin: 30px;
  padding: 50px;
  img {
    padding-right: 0;
    margin-right: -62.5%;
    right: 0;
    @media (max-width:400px){
      height:50%;
      width:50%;
    }
  }
  h1 {
    color:#939191;
    font-size: 2rem;
    font-weight: 600;
    @media (max-width:400px){
      font-size:1.3rem;
    }
  }
  p {
    font-size: 1.5rem;
    margin-left: 15px;
    @media (max-width:400px){
      font-size:0.8rem;
    }
  }
  @media (max-width:400px){
    height:75%;
    width:100%;
    margin:0;
    padding:0;
}

`;
const Heading=styled.h1`
    margin-left:30px;
    padding:2rem;
    font-weight:700;
    font-size:2.5rem;
    @media (max-width:400px){
      margin:0;
      padding:0.5rem;
      font-weight:700;
      font-size:1.8rem;
  }
`;

const Row = styled.div`
  text-align: auto;
  justify-between: space-between;
  display: flex;
  align-items: center;
  max-width: 1100px;
  padding: 30px;
  margin-left: 40px;
  @media (max-width:400px){
    width:100%;
    margin:0;
    padding:0;
    font-size:1rem;
}

`;

const Howitworks = () => {
  return (
    <>
      <Conatiner id="#Howitworks">
          <Heading>How it Works</Heading>
        <Row>
          <Left>
            <h1>Struck Ups, Facing issues Need Help?</h1>
            <p>The right place is here, Search here for right person.</p>
          </Left>
          <Right>
            <img src={search} alt="search"></img>
          </Right>
        </Row>
        <Row>
          <Left>
            <img src={perfect} alt="filter"></img>
          </Left>
          <Right>
            <h1> Look for the best one!</h1>
            <p> Find the perfect and wonderful matches.</p>
          </Right>
        </Row>
        <Row>
          <Left>
            <h1>Start sharing and get your needs!</h1>
            <p>Start Working together and do it quickly with sharing mind.</p>
          </Left>
          <Right>
            <img src={share} alt="reach and work"></img>
          </Right>
        </Row>
      </Conatiner>
    </>
  );
};

export default Howitworks;
