import React,{useEffect,useState} from "react";
import styled from "styled-components";
import Wheel from "./Wheel";
import Skills from "./Skills";
const Container = styled.div`
    top:10%;
    overflow:hidden;
    font-family: Montserrat;
    width:100%
    height: 250vh;
    background-color: #e5e5e5;
}`;

const Left = styled.div`

  h1 {
    color:#939191;
    font-size: 2rem;
    font-weight: 600;
  }
  p {
    font-size: 1rem;
  }
`;

const Right = styled.div`
  display: -webkit-box;
  margin: 0;
  padding: 0;
  img {
    padding-right: 0;
    margin-right: -10px;
    right: 0;
  }
  h1 {
      color:#939191;
    font-size: 2rem;
    font-weight: 600;
  }
  p {
    position:relative;
    font-size: 1rem;
    left:25%;
  }

`;
const Heading=styled.h1`
    margin-left:30px;
    padding:2rem;
    font-weight:800;
    font-size:2.5rem;
`;

const Row = styled.div`
  text-align: left;
  justify-between: space-evenly;
  display: flex;
  align-items: center;
  max-width: 1100px;
  padding: 30px;
  margin-left: 40px;
  @media screen and (max-width:670){
    width:100%;
  }
`;

const Projects = ({project}) => {
  const fetchURL = "http://localhost/lister/Taxonomy";
  const getItems = () => fetch(fetchURL).then(res => res.json());
  const [items, setItems] = useState();

  useEffect(() => {
     getItems().then(data => setItems(data));
  }, []);
  console.log(items)
  return (

    <> 
      <Container>

        <Heading>Projects and Skills</Heading>
        <Row>
          <Left id="top_left">{project.map(x =>(<p>{x.project_description}</p>))}</Left>
          <Right id="top_right"> <Wheel card={items}></Wheel></Right>

        </Row>
        <Row>
          <Left id="bottom_left"><Skills skill={items}></Skills></Left>
          <Right id="bottom_right">{project.map(x =>(<p>{x.skills_description}</p>))}</Right>
        </Row>

      </Container>

    </>
  );

};

export default Projects;
