import React, { Fragment } from "react";
import randomcolor from "randomcolor";
import {
    useParams,
  } from "react-router-dom";
import "../components/styles/orgChartStyles.css"
import empData from "../EmpData.json"

const Chart = () => {
    // const levelColor = randomcolor();
    let params = useParams();
    // let OrgChartArray = [];
    let head =[];
    window.sessionStorage.setItem("OrgLevel", 1);
    const ReportingPeople = (EmpName) => {
      if(JSON.parse(window.sessionStorage.getItem(EmpName))){
        // let ReportingArray = window.sessionStorage.getItem(EmpName);
        // console.log(ReportingArray);
        return JSON.parse(window.sessionStorage.getItem(EmpName));
      }

      let ReportingPeopleArr = [];
      let match = false;
      for(let i=0; i<empData.length; i++){
        let matching =false;
        if(!match && (empData[i].reports_to).includes(EmpName) ){  //checking for a match
          ReportingPeopleArr.push(empData[i]);
          match = true;
          matching = true;
        }else if(match && (empData[i].reports_to).includes(EmpName) ){ //after having match all the reporting employee will be in group
          ReportingPeopleArr.push(empData[i]);
          matching = true;
        }

        if(match && !matching){  //when the sequence of reporters broken then return the array
          window.sessionStorage.setItem(EmpName, JSON.stringify(ReportingPeopleArr));
          // console.log(JSON.parse(window.sessionStorage.getItem(EmpName)));
          console.log(ReportingPeopleArr);
          return ReportingPeopleArr;
        }

      }
      return ReportingPeopleArr;

    }
    if(params.Emp === "default"){
         head = [
            {
            "name": "Srinivasan Gopal",
            "emp_id": "0001",
            "role": "Chief Executive Officer",
            "DP": "/sites/default/files/default_images/DefaultDp_0.png",
        }];

        const Presidents = [
            {
                "name": "Vasan Sampath",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0011",
                "DP": "/sites/default/files/2021-07/chris-gayle_0.png",
                "role": "President"
                },
                {
                "name": "Mukund Devarajan",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0012",
                "DP": "/sites/default/files/2021-07/rishabh-rajendra-pant_0.png",
                "role": "Senior Vice President"
                },
                {
                "name": "Vijay Ramanan",
                "reports_to": "Srinivasan Gopal",
                "emp_id": "0013",
                "DP": "/sites/default/files/2021-07/rohit-sharma.png",
                "role": "Senior Vice President"
                }
        ]
        window.sessionStorage.setItem("Reporting", JSON.stringify(Presidents));

    }else{
      head = JSON.parse(window.sessionStorage.getItem("emp"));
      // head.push(JSON.parse(params.Emp));
      let arr = [];
      arr.push(params.Emp);
      console.log(params.Emp.name);
      console.log(arr);
      let EmpName = head[0].name;
      window.sessionStorage.setItem("Reporting", JSON.stringify(ReportingPeople(EmpName)));

    }
    console.log( params.Emp);
    return (
      <div className="org-tree">

      <Card data={head} />
   </div>

    )
}

export default Chart;

const Card = (props) => {
  const levelColor = randomcolor();
  console.log(props);
  let level = window.sessionStorage.getItem("OrgLevel") + 1;
  window.sessionStorage.setItem("OrgLevel", level);
  level = parseInt(level);
  let recursion = false;
  if(level === 11){
    recursion = true;
  }
  // console.log(level)

  const newChart  = (Emp) => {
    let EmpArr = [];
    EmpArr.push(JSON.parse(Emp));
    window.sessionStorage.setItem("emp", JSON.stringify(EmpArr))
    console.log(JSON.parse(window.sessionStorage.getItem("emp")));
    console.log(JSON.parse(Emp).name.length);
    window.location.href = "/OrgChart/" + Emp;
  }

  return (
    <>
    <ul>

      {props.data.map((item) => (
        <Fragment key={item.name}>
          <li>
            <div className="OrgCard"  onClick={() => newChart(JSON.stringify(item))}>
              <div className="image">
                {console.log(item)}
                {console.log(props.data.length)}
                <img src={`https://dev-employeeportal.pantheonsite.io/${item.DP}`}   alt="Profile"
                  style={{ borderColor: levelColor }}></img>
              </div>
              <div className="OrgCard-body">
                <h4 className="OrgCard-body-h4">{item.name}</h4>
                <p className="OrgCard-body-p">{item.role}</p>
              </div>
              <div></div>
            </div>
            {console.log(recursion)}
            {props.data.length === 1 ? <Card data={JSON.parse(window.sessionStorage.getItem("Reporting"))} /> : console.log(level)}
          </li>
        </Fragment>
      ))}
    </ul>
    </>
  );
};
