import React, { useEffect, Fragment, useState } from "react";
import randomcolor from "randomcolor";
import {
  useParams,
} from "react-router-dom";
import "../components/styles/orgChartStyles.css"
// import useFetch from '../utility'
import empData from "../EmpData.json";
import PlayCircleOutlineIcon from '@material-ui/icons/PlayCircleOutline';

const Chart = () => {
  // const levelColor = randomcolor();
  let params = useParams();


  let head = [];
  window.sessionStorage.setItem("OrgLevel", 1);
  const ReportingPeople = (EmpName) => {
    if (JSON.parse(window.sessionStorage.getItem(EmpName+" Reporting"))) {
      // let ReportingArray = window.sessionStorage.getItem(EmpName);
      // console.log(ReportingArray);
      return JSON.parse(window.sessionStorage.getItem(EmpName+" Reporting"));
    }

    let ReportingPeopleArr = [];
    let match = false;
    for (let i = 0; i < empData.length; i++) {
      let matching = false;
      if (!match && (empData[i].reports_to).includes(EmpName)) {  //checking for a match
        ReportingPeopleArr.push(empData[i]);
        match = true;
        matching = true;
      } else if (match && (empData[i].reports_to).includes(EmpName)) { //after having match all the reporting employee will be in group
        ReportingPeopleArr.push(empData[i]);
        matching = true;
      }

      if (match && !matching) {  //when the sequence of reporters broken then return the array
        window.sessionStorage.setItem(EmpName+" Reporting", JSON.stringify(ReportingPeopleArr));
        console.log(ReportingPeopleArr);
        return ReportingPeopleArr;
      }

    }
    return ReportingPeopleArr;

  }
  if (params.Emp === "Srinivasan Gopal") {
    head = [
      {
        "name": "Srinivasan Gopal",
        "emp_id": "0001",
        "role": "Chief Executive Officer",
        "DP": "/sites/default/files/default_images/DefaultDp_0.png",
      }];

    const Presidents = [
      {
        "name": "Vasan Sampath",
        "reports_to": "Srinivasan Gopal",
        "emp_id": "0011",
        "DP": "/sites/default/files/2021-07/chris-gayle_0.png",
        "role": "President"
      },
      {
        "name": "Mukund Devarajan",
        "reports_to": "Srinivasan Gopal",
        "emp_id": "0012",
        "DP": "/sites/default/files/2021-07/rishabh-rajendra-pant_0.png",
        "role": "Senior Vice President"
      },
      {
        "name": "Vijay Ramanan",
        "reports_to": "Srinivasan Gopal",
        "emp_id": "0013",
        "DP": "/sites/default/files/2021-07/rohit-sharma.png",
        "role": "Senior Vice President"
      }
    ]

    window.sessionStorage.setItem("ReportsTo", JSON.stringify(head));
    window.sessionStorage.setItem(head[0].name, JSON.stringify(head));

    for(let i=0; i< Presidents.length; i++){
      let EmpArr = [];
      EmpArr.push(JSON.stringify(Presidents[i])); 
      window.sessionStorage.setItem((Presidents[i]).name, JSON.stringify(EmpArr))
    }
    head = Presidents;
    let Reporting = [];
    window.sessionStorage.setItem("Reporting", JSON.stringify(Reporting));

  } else {
    head = JSON.parse(window.sessionStorage.getItem(params.Emp));
    // head.push(JSON.parse(params.Emp));
    let arr = [];
    arr.push(params.Emp);
    console.log(params.Emp);
    console.log(head);
    let EmpReportsTo = head[0].reports_to;
    console.log(EmpReportsTo);
    window.sessionStorage.setItem("Reporting", JSON.stringify(ReportingPeople(params.Emp)));

    if(EmpReportsTo){
      if(JSON.parse(window.sessionStorage.getItem(EmpReportsTo))){
        window.sessionStorage.setItem("ReportsTo",  (window.sessionStorage.getItem(EmpReportsTo)));
      }
      else{
        let ReportsToArr = [];
        for (let i = 0; i < empData.length; i++) {
          if ((empData[i].name).includes(head[0].reports_to)) {
          ReportsToArr.push(empData[i]);
          window.sessionStorage.setItem(empData[i].name, JSON.stringify(ReportsToArr));
          window.sessionStorage.setItem("ReportsTo", JSON.stringify((ReportsToArr)));
        }
      }
    }
    }else{
     let Ceo = [
        {
          "name": "Srinivasan Gopal",
          "emp_id": "0001",
          "role": "Chief Executive Officer",
          "DP": "/sites/default/files/default_images/DefaultDp_0.png",
        }];
        window.sessionStorage.setItem("ReportsTo", JSON.stringify(Ceo));
    }


  }
  console.log(params.Emp);
  return (
    <div className="org-tree">

      <Card data={head} />
    </div>

  )
}

export default Chart;

const Card = (props) => {
  const levelColor = randomcolor();
  console.log(props);

  //get the new emp details of desired emp (clicked) and show his org chart
  const newChart = (Emp) => {
    let EmpArr = [];
    EmpArr.push(JSON.parse(Emp)); 
    window.sessionStorage.removeItem("ReportsTo");
    let name = JSON.parse(Emp).name;
    window.sessionStorage.setItem(name, JSON.stringify(EmpArr))
    window.location.href = "/OrgChart/" + name;
  }

  return (
    <>
      <ul>
      {JSON.parse(window.sessionStorage.getItem("ReportsTo")).map((item) => (

          <Fragment key={item.name}>
            <li>
              <div className="OrgCard" onClick={() => newChart(JSON.stringify(item))}>
                <div className="image">
                  {console.log(item)}
                  {console.log(props.data.length)}
                  <img src={`https://dev-employeeportal.pantheonsite.io/${item.DP}`} alt="Profile"
                    style={{ borderColor: levelColor }}></img>
                </div>
                <div className="OrgCard-body">
                  <div className="name-profile-container">
                  <h4 className="OrgCard-body-h4">{item.name}</h4>
                  {/* <p> */}
                    <a><PlayCircleOutlineIcon className="playIcon"></PlayCircleOutlineIcon></a>
                  {/* </p> */}
                  </div>
                  <p className="OrgCard-body-p">{item.role}</p>
                </div>
                <div></div>
              </div>
      <ul>

      {props.data.map((item) => (
          <Fragment key={item.name}>
            <li>
              <div className="OrgCard" onClick={() => newChart(JSON.stringify(item))}>
                <div className="image">
                  {console.log(item)}
                  {console.log(props.data.length)}
                  <img src={`https://dev-employeeportal.pantheonsite.io/${item.DP}`} alt="Profile"
                    style={{ borderColor: levelColor }}></img>
                </div>
                <div className="OrgCard-body">
                  <h4 className="OrgCard-body-h4">{item.name}</h4>
                  <p className="OrgCard-body-p">{item.role}</p>
                </div>
                <div></div>
              </div>

              {JSON.parse(window.sessionStorage.getItem("Reporting")).length > 0 ?
              <ul>
                {JSON.parse(window.sessionStorage.getItem("Reporting")).map((item) => (
                  <Fragment key={item.name}>
                    <li>
                      <div className="OrgCard" onClick={() => newChart(JSON.stringify(item))}>
                        <div className="image">
                          {console.log(item)}
                          {console.log(props.data.length)}
                          <img src={`https://dev-employeeportal.pantheonsite.io/${item.DP}`} alt="Profile"
                            style={{ borderColor: levelColor }}></img>
                        </div>
                        <div className="OrgCard-body">
                          <h4 className="OrgCard-body-h4">{item.name}</h4>
                          <p className="OrgCard-body-p">{item.role}</p>
                        </div>
                        <div></div>
                      </div>
                    </li>
                  </Fragment>
                ))}
              </ul>
              :<></>}
            </li>
          </Fragment>
        ))}
      </ul>
      </li>
          </Fragment>
        ))}
      </ul>
    </>
  );
};
