import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Wheel from "./Wheel";
import Skills from "./Skills";
import he from 'he';
const Container = styled.div`
    top:10%;
    overflow:hidden;
    font-family: Montserrat;
    width:100%
    height: 250vh;
    background-color: #e5e5e5;
}`;

const Left = styled.div`
  h1 {
    color: #939191;
    font-size: 2rem;
    font-weight: 600;
  }
  p {
    font-size: 1rem;
  }
`;

const Right = styled.div`
  display: -webkit-box;
  margin: 0;
  padding: 0;
  img {
    padding-right: 0;
    margin-right: -10px;
    right: 0;
  }
  h1 {
    color: #939191;
    font-size: 2rem;
    font-weight: 600;
  }
  p {
    position: relative;
    font-size: 1rem;
    left: 25%;
  }
`;
const Heading = styled.h1`
  margin-left: 30px;
  padding: 2rem;
  font-weight: 800;
  font-size: 2.5rem;
`;

const Row = styled.div`
  text-align: left;
  justify-between: space-evenly;
  display: flex;
  align-items: center;
  max-width: 1100px;
  padding: 30px;
  margin-left: 40px;
  @media screen and (max-width: 670) {
    width: 100%;
  }
`;
const fetchURL = "http://localhost/lister/Taxonomy/Projects";

const Projects = ({ project }) => {
  const [projectData, setProjectData] = useState([]);

  useEffect(() => {
    const fetchFuntion = async () => {
      const data = await fetch(fetchURL);
      const res = await data.json();
      if (res.ok) {
        setProjectData(res);
      }
    };
    fetchFuntion();
  }, []);

  return (
    <>
      <Container id="Categories">
        <Heading>Projects and Skills</Heading>
        <Row>
          <Left id="top_left">
            {project &&
              project.map((x, index) => (

                <p key={index}>{he.decode(x.project_description.replace(/<[^>]+>/g, ''))}</p>
              ))}
          </Left>
          <Right id="top_right">
            {" "}
            {projectData && <Wheel card={projectData}></Wheel>}
          </Right>
        </Row>
        <Row>
          <Left id="bottom_left">
            <Skills skill={projectData}></Skills>
          </Left>
          <Right id="bottom_right">
            {project &&
              project.map((x, index) => (
                <p key={index}>{x.skills_description}</p>
              ))}
          </Right>
        </Row>
      </Container>
    </>
  );
};

export default Projects;
