import React, { Component } from 'react';
import '../components/styles/FilterPage.css';
import config from '../config';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';


export default class EmployeeView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Employees: [],
      skillsApi: [],
      MangersApi: [],
      PodsApi:[],
      projectsApi:[],
      skills: [],
      name: '',
      Experience: null,
      PrimarySkill: '',
      ExperienceManuallyMax: null,
      ExperienceManuallyMin: null,
      checkedItemsSkills: new Map(),
      checkedItemsManager: new Map(),
      checkedItemsPod: new Map(),
      checkedItemsProjects: new Map(),
      ExperienceMinMax: [0, 25],
    }

    this.SkillhandleChange = this.SkillhandleChange.bind(this);  
    this.ManagerHandleChange = this.ManagerHandleChange.bind(this);  
    this.PodsHandleChange = this.PodsHandleChange.bind(this);
    this.ProjectsHandleChange = this.ProjectsHandleChange.bind(this); 
  }

  componentDidMount() {
    Promise.all([
      fetch(config.drupal_url+'/lister/Employees').then(res => res.json()),
      fetch(config.drupal_url+'/lister/Taxonomy/skills').then(res => res.json()),
      fetch(config.drupal_url+'/lister/Taxonomy/manager').then(res => res.json()),
      fetch(config.drupal_url+'/lister/Taxonomy/pod').then(res => res.json()),
      fetch(config.drupal_url+'/lister/Taxonomy/projects').then(res => res.json()),
    ]).then(([urlOneData, urlTwoData, urlThreeData, urlFourData, urlFiveData]) => {
      this.setState({
        Employees: urlOneData,
        skillsApi: urlTwoData,
        MangersApi: urlThreeData,
        PodsApi:urlFourData,
        projectsApi:urlFiveData
      });
    })
  }

    valueText = (value) => {
        return `${value}`;
    };
    handleChange = (e, newValue) => {
        console.log(newValue);
        this.setState({ ExperienceMinMax: newValue });
    };

  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  }


  SkillhandleChange(event) {   //skills checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsSkills: prevState.checkedItemsSkills.set(item, isChecked) }));
  }

  ManagerHandleChange(event) {   //managers checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsManager: prevState.checkedItemsManager.set(item, isChecked) }));
  }

  PodsHandleChange(event) {   //pods checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsPod: prevState.checkedItemsPod.set(item, isChecked) }));
  }

  ProjectsHandleChange(event) {   //project checkeditems change
    var isChecked = event.target.checked;
    var item = event.target.value;
    this.setState(prevState => ({ checkedItemsProjects: prevState.checkedItemsProjects.set(item, isChecked) }));
  }



  render() {
    let Employees = this.state.Employees;
    let experienceMin, experienceMax;
    let expMinReq = this.state.ExperienceMinMax[0];
    let expMaxReq = this.state.ExperienceMinMax[1];
    let nameReq = this.state.name;
    let PrimarySkillReq = this.state.PrimarySkill;
    let primaryskillsChecked = this.state.checkedItemsSkills;
    let ManagersChecked = this.state.checkedItemsManager;
    let podsChecked = this.state.checkedItemsPod;
    // this.state.skills.filter(onlyUnique);removeusingSet(array)
    console.log((this.state.checkedItemsSkills));
    console.log((ManagersChecked));
    console.log((podsChecked));

    return (
      <div className="filter-container">
        <div className="reqForm">
          <form >
            <p className="filterName">Enter name or Id:</p>
            <input
              type='text'
              name='name'
              onChange={this.myChangeHandler}
              className="InputtextField" 
            />

            <div className="Experience-overview">
            <p className="filterName">Experience: </p>
            <p> {this.state.ExperienceMinMax[0]} - </p>
              <p>  {this.state.ExperienceMinMax[1]} years</p>
            </div>
            <div  style={{width:150}} >
                <Typography id="range-slider" gutterBottom>
                {/* Experience */}
                {/* <p>{this.state.ExperienceMinMax[0]} 
                {this.state.ExperienceMinMax[1]}</p> */}
                </Typography>
                <Slider
                defaultValue={[0, 25]}
                value={this.ExperienceMinMax}
                onChange={this.handleChange}
                valueLabelDisplay="auto"
                aria-labelledby="range-slider"
                getAriaValueText={this.valueText}
                step={1}
                min={0}
                max={40}
                />
            </div>

            {/* Priamry skills */}
            <p className="filterName">Skills :</p>


            <form >
            <table className="PrimarySkillsTableCB">
              {

                this.state.skillsApi.map(item => (
                  <tr>
                    <td>
                    <label>
                      <input
                        type="checkbox"
                        value={item.name.toLowerCase()}
                        onChange={this.SkillhandleChange}
                      /> {item.name}
                    </label>
                   </td>
                   </tr>
                ))
              }
            </table>
            </form>

            {/* Manager checkbox selection */}
            <p className="filterName">Managers :</p>
            <form >
            <table className="PrimarySkillsTableCB">
              { 
                this.state.MangersApi.map(item => (
                  <tr>
                    <td>
                    <label>
                      <input
                        type="checkbox"
                        value={item.name.toLowerCase()}
                        onChange={this.ManagerHandleChange}
                      /> {item.name}
                    </label>
                   </td>
                   </tr>
                ))
              }
            </table>
            </form>

            {/* Pods checkbox selection */}
             <p className="filterName">Pods :</p>
            <form >
            <table className="PrimarySkillsTableCB">
              { 
                this.state.PodsApi.map(item => (
                  <tr>
                    <td>
                    <label>
                      <input
                        type="checkbox"
                        value={item.name.toLowerCase()}
                        onChange={this.PodsHandleChange}
                      /> {item.name}
                    </label>
                   </td>
                   </tr>
                ))
              }
            </table>
            </form>

