import React, { Component } from 'react';
import '../components/styles/FilterPage.css';
// import '../CSS/FilterPage.css';
import config from '../config';
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
// import Slider from '@material-ui/core/Slider';
// import { Slider } from 'material-ui-slider';
// import Link from "react";



export default class FilterPage extends Component {

  constructor(props) {
    super(props);

    this.state = {
      Employees: [],
      name: '',
      Experience: null,
      PrimarySkill: '',
      ExperienceManuallyMax: null,
      ExperienceManuallyMin: null,
      ExperienceMinMax: [3, 7]
    }
  }


   handleChange = (event) => {
    this.setState({ExperienceMinMax: event.target.value});
  };
  componentDidMount() {
    fetch(config.drupal_url + '/lister/Employees')
      .then(x => x.json())
      .then(x => this.setState({ Employees: x }));
  }

  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  }


  render() {
    // let ExpMinMax = this.state.value;
    let Employees = this.state.Employees;
    let experienceMin, experienceMax;
    let expReq = this.state.Experience;
    let nameReq = (this.state.name);
    let PrimarySkillReq = this.state.PrimarySkill;
    let ExperienceManuallyMaxReq = this.state.ExperienceManuallyMax;
    let ExperienceManuallyMinReq = this.state.ExperienceManuallyMin;

    console.log(Employees);

    if (ExperienceManuallyMaxReq || ExperienceManuallyMinReq) {  //checking whether customer entered the custom experience or not
      experienceMin = ExperienceManuallyMinReq;
      experienceMax = ExperienceManuallyMaxReq;
      if (!ExperienceManuallyMaxReq) {
        experienceMax = 100;
      } else if (!ExperienceManuallyMinReq) {
        experienceMin = 0
      }
    } else {
      if (!expReq) {
        experienceMin = 0;
        experienceMax = 100;
        console.log(expReq);
      } else {
        var exp = expReq.split("-");
        experienceMin = exp[0];
        experienceMax = exp[1];
      }
    }

    console.log(experienceMax);
    console.log(experienceMin);
    return (
      <div className="filter-container">
        <div className="reqForm">
          <form >
            <p className="filterName">Enter name:</p>
            <input
              type='text'
              name='name'
              onChange={this.myChangeHandler}
            />
            <p className="filterName">Experience :</p>
            {/* <input
            type='text'
            name='Exp'
            onChange={this.myChangeHandler}
          /> */}
            {/* <input type="radio" name="Experience" onChange={this.myChangeHandler} value="0-4" />
            <label > 0 - 4 years</label><br />
            <input type="radio" name="Experience" onChange={this.myChangeHandler} value="5-7" />
            <label > 5 - 7 years</label><br />
            <input type="radio" name="Experience" onChange={this.myChangeHandler} value="8-10" />
            <label> 8-10 years</label><br />
            <input type="radio" name="Experience" onChange={this.myChangeHandler} value="11-15" />
            <label >11-15 years</label>

            <input
              type='text'
              name='ExperienceManuallyMin'
              onChange={this.myChangeHandler}
              className='experienceManuallyBox'
              placeholder='Min'
            />
            <input
              type='text'
              name='ExperienceManuallyMax'
              onChange={this.myChangeHandler}
              className='experienceManuallyBox'
              placeholder='Max'
            /> */}
            {/* <div >
              <Typography id="range-slider" gutterBottom>
                <p>Experience </p>
                <p>{this.state.value[0]} </p>
                {this.state.value[1]}
              </Typography>
              <Slider
              name='ExperienceMinMax'
                value={this.state.ExperienceMinMax}
                onChange={this.handleChange}
                valueLabelDisplay="auto"
                aria-labelledby="range-slider"
                getAriaValueText={valuetext}
              />
            </div> */}

            <p className="filterName">Primary Skill :</p>
            <input
              type='text'
              name='PrimarySkill'
              onChange={this.myChangeHandler}
            />
          </form>
        </div>

        <div className="Employees">

          {Employees.map(x => (
            <>
              {/* { console.log(x.Experience )}  */}
              {(
                ((((parseInt(x.Experience)) >= experienceMin) && (parseInt(x.Experience) <= experienceMax))    //experience
                  && ((nameReq === '') ? true : ((x.name.toLowerCase()).includes(nameReq.toLowerCase())))                     //name
                  && ((PrimarySkillReq === '') ? true : ((x.Primary_skills.toLowerCase()).includes(PrimarySkillReq.toLowerCase())))

                ) ?
                  (<div className="Employee-details">

                    {/* {nameReq}*/}
                    {/* { console.log(x.Experience )} 
              { console.log(experienceMin )} 
              { console.log(experienceMax )}  */}

                    <img src={`http://localhost/${x.Profile_photo}`}
                      height='200' alt="profilephoto"
                      className="profilephoto" />
                    {/* {x.name} */}
                    <div className="Employee-description">
                      <p className="Name">{x.name}</p>
                      {/* <p>{ x.Emp_id }</p> */}
                      <p>{x.role}</p>
                      <p>Manager: {x.Manager}</p>

                      <p>Experience: {x.Experience} years</p>

                    </div>
                    <button className="Details-Btn"><a href={'/EmployeeDetails/' + x.Emp_id}>Show Details</a></button>
                    {/* <Link to={{ pathname: "/EmployeeView", state: '{ x.Emp_id }' }}>{ x.name }</Link> */}

                  </div>)
                  : (<></>)
              )}


            </>
          ))
          }
        </div>
      </div>
    );
  }
}
